-- Drop the existing tables to start fresh
DROP TABLE IF EXISTS tourist_places;
DROP TABLE IF EXISTS user_itineraries;

-- Tourist Places Table with a 'type' column
CREATE TABLE IF NOT EXISTS tourist_places (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    name TEXT NOT NULL,
    state TEXT NOT NULL,
    description TEXT,
    budget TEXT,
    safety_level TEXT,
    population_crowd TEXT,
    image_url TEXT,
    type TEXT
);

-- User Itineraries Table
CREATE TABLE IF NOT EXISTS user_itineraries (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    title TEXT NOT NULL,
    start_date TEXT,
    end_date TEXT,
    itinerary_data TEXT,
    status TEXT
);

