INSERT INTO tourist_places (name, state, description, budget, safety_level, population_crowd, image_url, type) VALUES
('Taj Mahal', 'Uttar Pradesh', 'An immense mausoleum of white marble...', 'Medium', 'High', 'High', 'https://upload.wikimedia.org/wikipedia/commons/d/da/Taj-Mahal.jpg', 'Historical'),
